import React, {Component} from 'react';
import EmployeeService from "../services/EmployeeService";

class CreateEmployeeComponent extends Component {
    constructor() {
        super();
        this.state = {
            firstName: "",
            lastName: "",
            emailId: ""

        }
        this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this)
        this.changeLastNameHandler = this.changeLastNameHandler.bind(this)
        this.changeEmailId = this.changeEmailId.bind(this)

        this.saveEmployee = this.saveEmployee.bind(this)
        this.cancel = this.cancel.bind(this)
    }

    changeFirstNameHandler = (event) => {
        this.setState({
            firstName: event.target.value
        })
    }

    changeLastNameHandler = (event) => {
        this.setState({
            lastName: event.target.value
        })
    }
    changeEmailId = (event) => {
        this.setState({
            emailId: event.target.value
        })
    }

    saveEmployee = (e) => {
        e.preventDefault();
        let employee = {firstName: this.state.firstName, lastName: this.state.lastName, emailId: this.state.emailId}
        console.log('employee =>' + JSON.stringify(employee))

        EmployeeService.createEmployee(employee)
            .then(res => {
                this.props.history.push('/employees')
            })
    }

    cancel() {
        this.props.history.push('/employees');
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        <h3 className="text-center table-bordered border-dark bg-dark text-white">Add Employee</h3>
                        <div className="card-body">
                            <form>
                                <div className="form-group">
                                    <label>First Name</label>
                                    <input placeholder="First Name" name="firstName" className={"form-control"}
                                           value={this.state.firstName} onChange={this.changeFirstNameHandler}/>
                                </div>
                                <div className={"form-group"}>
                                    <label>Last Name</label>
                                    <input placeholder={"Last Name "} name={"lastName"} className={"form-control"}
                                           value={this.state.lastName} onChange={this.changeLastNameHandler}/>

                                </div>

                                <div className={"form-group"}>
                                    <label>Email Id</label>
                                    <input type={"email"} placeholder={"Email Id "} name={"email"}
                                           className={"form-control"}
                                           value={this.state.emailId} onChange={this.changeEmailId}/>
                                </div>

                                <button  className={"btn btn-success"} onClick={this.saveEmployee}>Save</button>
                                <button style={{marginLeft:"120px"}}className={"btn btn-danger"} onClick={this.cancel}>Cancel</button>
                            </form>
                        </div>
                    </div>
                </div>

            </div>
        );
    }
}

export default CreateEmployeeComponent;
